               "host": dev.modbus_ip,
               "port": dev.modbus_port,
               "address": dev.address,
               "quantity": dev.quantity,
               "payload": m_response
            });
            socket.end();
            recon.end();
         });
      socket.end();
      recon.end();
   }, console.error);

   socket.on('timeout', () => {
      console.log('socket timeout');
      flow.set("aguarda", false);
      recon.end();
      socket.end();
   });

